console.log("Content script starting - Hidden Average");chrome.storage.sync.get(["hideAverage"]).then(d=>{if(d.hideAverage){var e=document.createElement("link");e.href=chrome.runtime.getURL("hide.css"),e.id="hiddenAverageStyle",e.type="text/css",e.rel="stylesheet",document.getElementsByTagName("head")[0].appendChild(e)}});chrome.storage.sync.onChanged.addListener(d=>{var a;const e=d.hideAverage.oldValue,r=d.hideAverage.newValue;if(e&&!r)(a=document.getElementById("hiddenAverageStyle"))==null||a.remove();else if(!e&&r){var t=document.createElement("link");t.href=chrome.runtime.getURL("hide.css"),t.id="hiddenAverageStyle",t.type="text/css",t.rel="stylesheet",document.getElementsByTagName("head")[0].appendChild(t)}});
